package org.developers;import org.developers.model.Users.Position;import org.developers.model.Users.Role;import org.developers.model.Users.impl.Employee;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;import java.time.LocalDate;public class Start {    public static void main(String[] args) {        ApplicationContext ctx = new ClassPathXmlApplicationContext("all-context.xml");        Role coWorker = ctx.getBean("co-worker", Role.class);        Position managerStorePosition = ctx.getBean("manager-store", Position.class);        Employee managerStore = new Employee("Трофимова", "Людмила", "Николаевна", LocalDate.of(1978, 12, 12), coWorker, managerStorePosition);        System.out.println(managerStore.getName("abbr"));        System.out.println(managerStore.getPosition().getTitle());        System.out.println(managerStore.getRole().getName());        managerStore.getRole().getRights().forEach((right, value)->System.out.println(right + ": " + value));    }}